#! /usr/bin/env python

#Counting DNA Nucleotides

DNA=''
print(DNA.count('A'), end = ' ')
print(DNA.count('C'), end = ' ')
print(DNA.count('G'), end = ' ')
print(DNA.count('T'))
print('=' * 30)

#Transcribing DNA into RNA
DNA_1 = ""
RNA_1 = DNA_1.replace('T', 'U')
print(RNA_1)
print('=' * 30)

#Complementing a Strand of DNA
DNA_2 = ""
CPS = DNA_2.replace('A', 't')
CPS = CPS.replace('C', 'g')
CPS = CPS.replace('T', 'a')
CPS = CPS.replace('G', 'c')
CPS = CPS.upper()
print(CPS[::-1])
print('=' * 30)

